import React, { Component } from "react";

import { MenuButtonStyle } from "../../2_styles/3_module/0_buttons/MenuButtonStyle";
import Menu from "../Menu";

let menuActive = false;

export default class MenuButton extends Component {
  constructor() {
    super();
    this.toggleMenu = this.toggleMenu.bind(this);
  }

  toggleMenu() {
    const menuBox = document.getElementById("menu_box");
    const links = document.querySelectorAll(".menu_link");

    if (!menuActive) {
      menuBox.classList.contains("menu_box-hide") &&
        menuBox.classList.remove("menu_box-hide");
      menuBox.classList.add("menu_box-show");

      links.forEach(link => {
        link.classList.contains("link_hide") &&
          link.classList.remove("link_hide");
        link.classList.add("link_show");
      });

      menuActive = true;
    } else {
      menuBox.classList.remove("menu_box-show");
      menuBox.classList.add("menu_box-hide");

      links.forEach(link => {
        link.classList.contains("link_show") &&
          link.classList.remove("link_show");
        link.classList.add("link_hide");
      });

      menuActive = false;
    }
  }

  render() {
    return (
      <div>
        <div id="menu_btn-box">
          <MenuButtonStyle>
            <button id="menu_btn" onClick={this.toggleMenu}>
              <span></span>
              <span></span>
              <span></span>
            </button>
          </MenuButtonStyle>
        </div>
        <Menu />
      </div>
    );
  }
}
