import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";

//COMPONENTS
import HomeButton from "./0_components/0_buttons/HomeButton";
import MenuButton from "./0_components/0_buttons/MenuButton";
import Navbar from "./0_components/0_buttons/Navbar";

// PAGES
import Home from "./1_pages/Home";
import TheInitiative from "./1_pages/TheInitiative";
import TheOriginalSix from "./1_pages/TheOriginalSix";
import BecomeAnAvenger from "./1_pages/BecomeAnAvenger";

//STYLES
import { GlobalStyle } from "./2_styles/0_base/GlobalStyle";
import { BgBlur } from "./2_styles/3_module/MenuStyles";

function App() {
  return (
    <Router>
      <div id="App">
        <div id="main_box">
          <Route
            render={({ location }) => (
              <TransitionGroup>
                <CSSTransition
                  key={location.key}
                  timeout={3000}
                  classNames="fade"
                >
                  <BgBlur className="blur">
                    <Switch location={location}>
                      <Route path="/" exact component={Home} />
                      <Route path="/the-initiative" component={TheInitiative} />
                      <Route
                        path="/the-original-six"
                        component={TheOriginalSix}
                      />
                      <Route
                        path="/become-an-avenger"
                        component={BecomeAnAvenger}
                      />
                    </Switch>
                  </BgBlur>
                </CSSTransition>
              </TransitionGroup>
            )}
          />
        </div>
        <Navbar />
        {/* <HomeButton /> */}
        {/* <MenuButton /> */}
      </div>
      <GlobalStyle />
    </Router>
  );
}

export default App;
