    .menu_bgLogo {
      width: 100%;
      height: 100%;
      background-color: grey;

      .bgLogo_img {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;

        background-image: url(${props => props.bgLogo});
        background-size: cover;
        background-repeat: no-repeat;
      }
    }

