import styled from "styled-components";

export const HeaderStyle = styled.div`
  .bgImg_shadow {
    width: 100%;
    height: 500px;

    position: absolute;
    top: 0;
    left: 0;

    /* background: linear-gradient(to top, #111111, transparent 60%); */
    background: linear-gradient(to bottom, transparent, #111111 5%);
  }

  .bgImg {
    width: 100%;
    height: 500px;

    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;

    background-image: url(${props => props.bgImg});
    background-position: 30%;
    background-size: cover;
  }
`;
