import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";

//COMPONENTS
import HomeButton from "./0_components/0_buttons/HomeButton";
import MenuButton from "./0_components/0_buttons/MenuButton";
import Navbar from "./0_components/0_buttons/Navbar";

// PAGES
import Home from "./1_pages/Home";
import TheInitiative from "./1_pages/TheInitiative";
import TheOriginalSix from "./1_pages/TheOriginalSix";
import BecomeAnAvenger from "./1_pages/BecomeAnAvenger";

//STYLES
import { GlobalStyle } from "./2_styles/0_base/GlobalStyle";

export default class App extends Component {
  constructor() {
    super();
    this.state = { pageName: "" };
  }
  getPageName = () => {
    window.location.href === "http://localhost:3000/the-initiative" &&
      this.setState({ pageName: "the initiative" });

    window.location.href === "http://localhost:3000/the-original-six" &&
      this.setState({ pageName: "the original six" });

    window.location.href === "http://localhost:3000/become-an-avenger" &&
      this.setState({ pageName: "become an avenger" });
  };

  render() {
    const { pageName } = this.state;

    return (
      <Router>
        <div id="App">
          <div id="main_box">
            <Route
              render={({ location }) => (
                <TransitionGroup>
                  <CSSTransition
                    key={location.key}
                    timeout={1500}
                    classNames="fade"
                  >
                    <Switch onChange={this.getPageName} location={location}>
                      <Route path="/" exact component={Home} />
                      <Route path="/the-initiative" component={TheInitiative} />
                      <Route
                        path="/the-original-six"
                        component={TheOriginalSix}
                      />
                      <Route
                        path="/become-an-avenger"
                        component={BecomeAnAvenger}
                      />
                    </Switch>
                  </CSSTransition>
                </TransitionGroup>
              )}
            />
          </div>
          <Navbar pageName={pageName} />
          {/* <HomeButton /> */}
          {/* <MenuButton /> */}
        </div>
        <GlobalStyle />
      </Router>
    );
  }
}
