import React, { Component } from "react";
import { Parallax, Background } from "react-parallax";

import { BackgroundImgStyle } from "../../2_styles/1_layout/BackgroundImgStyle";

export default class BackgroundImg extends Component {
  render() {
    const {
      bgImg,
      bgImgBlur,
      top,
      height,
      zIndexImg,
      zIndexImgShadow
    } = this.props;
    return (
      <div>
        <div className="parallax">
          <Parallax
            bgImage={bgImg}
            bgImageAlt="the cat"
            strength={300}
            blur={{ min: -10, max: 20 }}
            bgClassName={"parallax_bg"}
          >
            <div className="bgImg_shadow" style={{ height: "100vh" }} />
          </Parallax>
        </div>
      </div>
    );
  }
}
