import React, { Component } from "react";
import * as Scroll from "react-scroll";
import {
  Link,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
  scroller
} from "react-scroll";

import { PageNavigationStyle } from "../../2_styles/3_module/PageNavigationStyle";

export default class PageNavigation extends Component {
  componentDidMount() {
    this.togglePageNavDots();
  }

  togglePageNavDots = () => {
    let prevScrollpos = window.pageYOffset;

    window.onscroll = function() {
      let currentScrollPos = window.pageYOffset;

      if (prevScrollpos < 5) {
        document.querySelector(".dots_box").style.opacity = "0";
      } else {
        document.querySelector(".dots_box").style.opacity = "1";
      }
      prevScrollpos = currentScrollPos;
    };
  };

  render() {
    const {
      section1,
      section2,
      section3,
      section4,
      section5,
      section6,
      section7,
      section8
    } = this.props;

    return (
      <div>
        <PageNavigationStyle>
          <div className="dots_box">
            <Link
              activeClass="active"
              to={section1}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section2}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section3}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section4}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section5}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section6}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section7}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>

            <Link
              activeClass="active"
              to={section8}
              spy={true}
              smooth={true}
              duration={500}
            >
              <div className="dots"></div>
            </Link>
          </div>
        </PageNavigationStyle>
      </div>
    );
  }
}
