import React, { Component } from "react";
import Parallax from "react-rellax";

import bgImgBanner from "../3_images/4_becomeAnAvenger/bg_become-banner.jpg";
import bgImgForm from "../3_images/4_becomeAnAvenger/bg_become-form.jpg";
import iconArrow from "../3_images/4_becomeAnAvenger/icon_arrow-form.svg";

import Header from "../0_components/1_layout/Header";
import Footer from "../0_components/1_layout/Footer";

import { BecomeAnAvengerStyle } from "../2_styles/1_layout/BecomeAnAvenger";

export default class BecomeAnAvenger extends Component {
  constructor(props) {
    super(props);
    this.state = {
      question: ["What is your superhero name?", "What is your real name?"],
      superName: "",
      realName: "",
      abilities: [],
      country: "",
      retiredHuman: false
    };
  }

  handleClick = () => {
    this.setState({ question: this.state.question[1] });
  };

  inputOnFocus = () => {
    document.querySelector(".form_bgImg").classList.add("from_bgImg-blur");
  };

  inputOnBlur = () => {
    document.querySelector(".form_bgImg").classList.remove("from_bgImg-blur");
  };

  render() {
    const { question } = this.state;

    return (
      <div>
        <Header
          bgImg={bgImgBanner}
          title="become an avenger"
          subtitle="join earthâ€™s mightiest heroes"
          bgPosition="80%"
        />

        <BecomeAnAvengerStyle bgImgForm={bgImgForm}>
          <form className="form_box">
            <div className="form_bgImg-box">
              <div className="form_bgImg"></div>
            </div>

            <Parallax speed={2}>
              <div className="form_input-box">
                <p>{question}</p>

                <input
                  onFocus={this.inputOnFocus}
                  onBlur={this.inputOnBlur}
                  placeholder="Chapolin Colorado"
                  type="text"
                />

                <div className="arrow_box">
                  <img
                    className="arrow_img"
                    src={iconArrow}
                    alt="form submit button"
                  />
                </div>

                <div className="line"></div>
              </div>
            </Parallax>

            <div className="dots_box">
              <div className="dot"></div>
              <div className="dot"></div>
              <div className="dot"></div>
              <div className="dot"></div>
              <div className="dot"></div>
            </div>
          </form>
        </BecomeAnAvengerStyle>

        <Footer />
      </div>
    );
  }
}
