import styled from "styled-components";

export const HeaderStyle = styled.div`
  position: relative;
  overflow: hidden;

  .header-text {
    height: 100vh;

    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

    font-family: "Cormorant Infant", serif;
    text-align: center;

    padding: 0 5vw 0 5vw;
  }

  .title {
    font-size: 3em;
    text-transform: uppercase;
    margin-bottom: 1px;
  }

  .pretitle,
  .subtitle {
    font-size: 1.5em;
  }

  .pretitle {
    margin-bottom: -5px;
  }

  .subtitle {
    margin-top: 10px;
  }

  @media only screen and (min-width: 600px) {
    .title {
      font-size: 6em;
    }

    .pretitle {
      margin-bottom: -10px;
    }

    .pretitle,
    .subtitle {
      font-size: 2em;
    }
  }
`;
