import React, { Component } from "react";

import bgImgHeader from "../3_images/2_theInitiative/bg_initiative-banner.jpg";
import bgImgSection1 from "../3_images/2_theInitiative/bg_initiative-section-1.jpg";
import bgImgSection2 from "../3_images/2_theInitiative/bg_initiative-section-2.jpg";
import bgImgSection3 from "../3_images/2_theInitiative/bg_initiative-section-3.jpg";
import bgImgSection4 from "../3_images/2_theInitiative/bg_initiative-section-4.jpg";

import Header from "../0_components/1_layout/Header";
import Section from "../0_components/1_layout/Section";
import Footer from "../0_components/1_layout/Footer";

const contentful = require("contentful");

export default class TheInitiative extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      images: {
        header: "",
        section1: "",
        section2: "",
        section3: "",
        section4: ""
      }
    };

    this.getData = this.getData.bind(this);
  }

  getData = async () => {
    const client = contentful.createClient({
      space: "b4lkqx9hlu6y",
      accessToken: "7ehrQIlJJLgAlYL9qMa4CeLdxIdiUJWrbtPOWpQThBc"
    });

    client
      .getEntries({ content_type: "theInitiative" })
      .then(response => {
        this.setState({
          data: response.items[0].fields,

          images: {
            header: response.items[0].fields.bgImgHeader.fields.file.url,
            section1: response.items[0].fields.bgImgSection1.fields.file.url,
            section2: response.items[0].fields.bgImgSection2.fields.file.url,
            section3: response.items[0].fields.bgImgSection3.fields.file.url,
            section4: response.items[0].fields.bgImgSection4.fields.file.url
          }
        });
      })
      .catch(console.error);
  };

  componentDidMount() {
    this.getData();
  }

  render() {
    const { data, images } = this.state;

    return (
      <div>
        <Header
          bgImg={images.header}
          title={data.title}
          subtitle={data.subtitle}
        />

        <Section
          bgImg={images.section1}
          title={data.section1Title}
          description={[<p></p>]}
        />

        <Section bgImg={images.section2} title={data.section2Title} />

        <Section bgImg={images.section3} title={data.section3Title} />

        <Section bgImg={images.section4} title={data.section4Title} />

        <Footer />
      </div>
    );
  }
}
