import React, { Component } from "react";
import { Link } from "react-router-dom";

import bgLogo from "../3_images/1_home/icon_home-big.png";

import { MenuStyle } from "../2_styles/3_module/MenuStyle";
import Line from "../0_components/Line";

export default class Menu extends Component {
  toggleMenu = () => {
    this.props.toggleMenu();
  };

  render() {
    return (
      <MenuStyle bgImg={bgImg}>
        <div id="menu_box" bgLogo={bgLogo}>
          <div className="menu_bgLogo">
            {/* <img id="bgLogo_img" src={bgLogo} alt="avengers logo" /> */}
          </div>
          <div className="menu_items">
            <Link
              onClick={this.toggleMenu}
              className="link_initiative"
              to="/the-initiative"
            >
              <div className="menu_link initiative ">The Initiative</div>
            </Link>

            <div className="line menu_line menu_line-top">
              <Line opacity="0.3" width="40vw" />
            </div>

            <Link
              onClick={this.toggleMenu}
              className="link_original"
              to="/the-original-six"
            >
              <div className="menu_link original">The Original Six</div>
            </Link>

            <div className="line menu_line delay menu_line-bottom">
              <Line opacity="0.3" width="40vw" />
            </div>

            <Link
              onClick={this.toggleMenu}
              className="link_become"
              to="/the-become-an-avenger"
            >
              <div className="menu_link become">Become An Avenger</div>
            </Link>
          </div>
        </div>
      </MenuStyle>
    );
  }
}
