import React from "react";
import { Parallax } from "react-parallax";

import { SectionStyle } from "../../2_styles/1_layout/SectionStyle";

export default function Section({ left, bgImg, title, description }) {
  return (
    <SectionStyle left={left}>
      <div className="parallax">
        <Parallax
          bgImage={bgImg}
          bgImageAlt="the cat"
          strength={300}
          blur={{ min: -8, max: 20 }}
          bgClassName={"parallax_bg"}
        >
          <div className="bgImg_shadow" style={{ height: "100vh" }} />
        </Parallax>
      </div>

      <div className="text">
        <div className="title"> {title} </div>
        <div className="description">{description}</div>
      </div>
    </SectionStyle>
  );
}
