import styled from "styled-components";

export const BecomeAnAvengerStyle = styled.div``;

const BgImg = styled.div``;

const Form = styled.div`
  #myForm {
    height: 150vh;
    padding: 0 5vw 0 5vw;

    display: flex;
    align-items: center;
    flex-wrap: wrap;

    /* background: lightcoral; */
  }

  .input_row {
    width: 100%;

    display: flex;
    flex-direction: column;
    margin-bottom: 1rem;

    /* background: grey; */
  }

  label {
    font-size: 1rem;
    font-family: "cormorant infant", serif;
  }
  input {
    padding: 1rem 1rem 1rem 0;

    background: linear-gradient(to right, #f0f0f0, transparent) left bottom
      no-repeat;
    background-size: 100% 1px;

    font-size: 1rem;
    font-family: "roboto", sans-serif;
    font-weight: 200;
    text-transform: uppercase;

    ::placeholder {
      color: #c7c7c7;
    }
  }

  .btn_submit-box {
    width: 100%;

    display: flex;
    justify-content: flex-end;
  }

  #btn_submit {
    cursor: pointer;
    text-align: end;
  }

  .dots_box {
    display: flex;
    justify-content: center;
    align-items: center;

    margin: auto;
    margin-top: -5rem;
  }

  .dot {
    background: #f0f0f0;
    width: 0.5rem;
    height: 0.5rem;
    border-radius: 100%;
    margin: 1rem;
  }

  /* ERRORS -------------------------------------- */

  .input_error {
    background: linear-gradient(to right, crimson, transparent) left bottom
      no-repeat;
    background-size: 100% 1px;
  }

  .form_error-message {
    margin-top: 1rem;
    color: crimson;
    font-weight: 300;
  }
`;
