import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { FormStyle } from "../../2_styles/3_module/FormStyle";

const validationSchema = Yup.object().shape({
  name: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Must enter a name"),
  email: Yup.string()
    .email("Must be a valid email")
    .max(255, "Must be shorter than 255")
    .required("Must enter a email")
});

export default class Form extends Component {
  render() {
    return (
      <div>
        <Formik>
          <FormStyle
            initialValues={{ name: "", email: "" }}
            validationSchema={validationSchema}
            onSubmit={(values, { setSubmitting, resetForm }) => {
              setSubmitting(true);
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                resetForm();
                setSubmitting(false);
              }, 500);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting
            }) => (
              <form id="myForm" onSubmit={handleSubmit}>
                <div className="input_row">
                  <label htmlFor="superName">
                    What's is your superhero name?
                    <input
                      type="text"
                      name="superName"
                      id="superName"
                      placeholder="Chapolin Colorado"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.name}
                      className={
                        touched.name && errors.name ? "has-error" : null
                      }
                    />
                  </label>
                </div>

                <div className="input_row">
                  <label htmlFor="realName">
                    What's is your real name?
                    <input
                      type="text"
                      name="realName"
                      id="realName"
                      placeholder="Peter Parker"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.name}
                      className={
                        touched.name && errors.name ? "has-error" : null
                      }
                    />
                  </label>
                </div>

                <div className="input-row">
                  <button type="submit" disabled={isSubmitting}>
                    Submit
                  </button>
                </div>
              </form>
            )}
          </FormStyle>
        </Formik>
      </div>
    );
  }
}
