    super();
    this.state = { data: {} };
  }

  componentDidMount() {
    const contentful = require("contentful");

    const client = contentful.createClient({
      space: "6kayr7q3017v",
      accessToken: "GXzS1zEIgwaj5Oy222thRvmVi1X7OGE_CxDLep-0SDo"
    });

    client
      .getEntries({ content_type: "test" })
      .then(response => {
        this.setState({ data: response.items });
        console.log(this.state.data);
      })
      .catch(console.error);
  }

  render() {
    return (
      <div>
