import styled from "styled-components";

export const FormStyle = styled.div`
  #myForm {
    height: 100vh;
    padding: 0 5vw 0 5vw;

    display: flex;
    align-items: center;
    flex-wrap: wrap;
  }

  .input_row {
    /* background: darkgray; */

    width: calc(100% - 2rem);

    display: flex;
    flex-direction: column;
    margin-bottom: 1rem;
    margin-top: 7rem;

    display: none;

    &:nth-child(1) {
      display: flex;
    }
  }

  label {
    font-size: 2rem;
    font-family: "cormorant infant", serif;
  }
  input {
    padding: 1rem 1rem 1rem 0;

    font-size: 1.5rem;
    font-family: "roboto", sans-serif;
    font-weight: 100;

    ::placeholder {
      color: rgba(240, 240, 240, 0.5);
    }
  }

  .line {
    height: 1px;
    width: 100%;

    align-self: flex-start;
    margin-top: -6rem;

    background: linear-gradient(to right, #f0f0f0, transparent);
  }

  .submit_button-box {
    width: 2rem;

    margin-top: 10rem;
    float: left;
    text-align: center;
  }

  #submit_button-icon {
    filter: invert(100);
    width: 1rem;
  }
`;
