import React, { Component } from "react";
import * as Scroll from "react-scroll";
import {
  Link,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
  scroller
} from "react-scroll";

import { PageNavigationStyle } from "../../2_styles/3_module/PageNavigationStyle";

export default class PageNavigation extends Component {
  componentDidMount() {
    this.togglePageNavDots();
    this.dotClicked();
  }

  togglePageNavDots = () => {
    let prevScrollpos = window.pageYOffset;

    window.addEventListener("scroll", function() {
      let currentScrollPos = window.pageYOffset;

      prevScrollpos < currentScrollPos &&
        (document.querySelector(".dots_box").style.opacity = "1");

      currentScrollPos <= 100 &&
        (document.querySelector(".dots_box").style.opacity = "0");

      prevScrollpos = currentScrollPos;
    });
  };

  dotClicked = () => {
    const dots = document.querySelectorAll(".dots");

    dots.forEach(dot => {
      dot.addEventListener("click", () => {
        dot.classList.remove("dot_active");
      });
    });

    dots.forEach(dot => {
      dot.addEventListener("click", () => {
        dot.classList.add("dot_active");
      });
    });
  };

  render() {
    const {
      section1,
      section2,
      section3,
      section4,
      section5,
      section6,
      section7,
      section8
    } = this.props;

    return (
      <div>
        <PageNavigationStyle>
          <div id="nav_dots">
            <div className="dots_box">
              <Link
                activeClass="active"
                to={section1}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section2}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section3}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section4}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section5}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section6}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section7}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>

              <Link
                activeClass="active"
                to={section8}
                spy={true}
                smooth={true}
                duration={500}
              >
                <div className="dots"></div>
              </Link>
            </div>
          </div>
        </PageNavigationStyle>
      </div>
    );
  }
}
