import styled from "styled-components";

export const HeaderStyle = styled.div`
  .title_box {
    position: absolute;
    top: 0;
    left: 50%;
  }

  .title {
    height: 100vh;

    position: relative;
    left: -50%;

    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

    font-family: "Cormorant Infant", serif;
    font-size: 7vw;
    text-align: center;

    p:nth-child(2) {
      font-size: 16vw;
      text-transform: uppercase;
    }
  }
`;
