import React, { Component } from "react";

import { Formik } from "formik";
import * as Yup from "yup";
import Parallax from "react-rellax";

import submitIcon from "../../3_images/4_becomeAnAvenger/icon_submit.svg";
import { FormStyle } from "../../2_styles/3_module/FormStyle";

import FormError from "../2_module/FormError";

const validationSchema = Yup.object().shape({
  superName: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Must enter a name"),
  realName: Yup.string()
    .email("Must be a valid email")
    .max(255, "Must be shorter than 255")
    .required("Must enter a email"),
  superpower: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Must enter at least one superpower"),
  country: Yup.string() // CREATE COUNTRY LIST
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Must enter at least one superpower"),
  retiredHuman: Yup.string() // BOOLEAN??
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Must enter at least one superpower"),
  email: Yup.string()
    .email("Must be a valid email")
    .max(255, "Must be shorter than 255 characters")
    .required("Must enter at least one superpower")
});

export default class Form extends Component {
  changeInput = () => {
    alert("hi");
  };

  render() {
    return (
      <div>
        <FormStyle>
          <Formik
            initialValues={{
              superName: "",
              realName: "",
              superpower: "",
              country: "",
              retiredHuman: "",
              email: ""
            }}
            validationSchema={validationSchema}
            onSubmit={(values, { setSubmitting, resetForm }) => {
              setSubmitting(true);
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                resetForm();
                setSubmitting(false);
              }, 500);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting
            }) => (
              <form id="myForm" onSubmit={handleSubmit}>
                <div className="input_row">
                  <label htmlFor="superName">
                    What is your superhero name?
                  </label>
                  <input
                    type="text"
                    name="superName"
                    id="superName"
                    placeholder="Chapolin Colorado"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.superName}
                    className={
                      touched.superName && errors.superName
                        ? "input_error"
                        : null
                    }
                  />
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <button
                  type="submit"
                  id="nextQuestion_button"
                  disabled={isSubmitting}
                >
                  <img
                    id="nextQuestion_button-icon"
                    src={submitIcon}
                    alt="submit button"
                  />
                </button>
              </form>
            )}
          </Formik>
        </FormStyle>
      </div>
    );
  }
}
