import styled from "styled-components";

export const PageNavigationStyle = styled.div`
  #nav_dots {
    height: 100vh;
    width: 100%;

    position: absolute;
    top: 4vh;

    display: flex;
    justify-content: center;
    z-index: 2;
  }

  .dots_box {
    position: fixed;

    display: flex;
    align-items: center;

    transition: 0.5s ease-in-out;
    opacity: 0;
  }

  .dots {
    width: 0.6rem;
    height: 0.6rem;

    margin-left: 1rem;

    /* background-color: #f0f0f0; */
    opacity: 0.5;
    border-radius: 100%;
    border: 1px solid white;
    cursor: pointer;

    transition: 0.3s ease-in-out;
    &:hover {
      transform: scale(1.5);
      opacity: 1;
    }
  }

  @media only screen and (min-width: 600px) {
    #nav_dots {
      top: 9vh;
    }
  }
`;
