import styled from "styled-components";

export const SectionStyle = styled.div`
  margin-bottom: 5em;
  height: 100%;

  .parallax {
    position: absolute;
    height: 100vh;
    width: 100%;
    z-index: -1;
  }

  .parallax > div {
    position: absolute;
  }

  .parallax_bg {
    left: 86% !important;
  }

  .bgImg_shadow {
    width: 100%;
    height: 100vh;

    background: linear-gradient(to bottom, transparent, #111111);
  }

  .text {
    padding: 70vh 5vw 0 5vw;
  }

  .title {
    text-align: center;
    text-transform: uppercase;

    font-family: "Cormorant Infant", serif;
    font-size: 2em;

    letter-spacing: 1px;

    margin-bottom: 20px;

    @media only screen and (min-width: 600px) {
      .title {
        color: red;
      }
    }
  }

  .description {
    text-align: justify;
    text-justify: inter-word;
  }

  p {
    margin-bottom: 1em;
    text-indent: 10px;
  }
`;
