import styled from "styled-components";

export const FormStyle = styled.div`
  #myForm {
    background: lightgray;

    height: 100vh;
    padding: 0 5vw 0 5vw;
  }

  .input_row {
    background: darkgray;

    width: calc(100% - 5rem);

    display: flex;
    flex-direction: column;
    margin-bottom: 1rem;

    display: none;

    &:nth-child(1) {
      display: flex;
    }
  }

  label {
    font-size: 2rem;
    font-family: "cormorant infant", serif;
  }
  input {
    font-size: 1.5rem;
    font-family: "roboto", sans-serif;
    font-weight: 100;

    ::placeholder {
      color: rgba(240, 240, 240, 0.5);
    }
  }

  .submit_button-box {
    background: grey;
    width: 2rem;
    text-align: center;
  }

  #submit_button-icon {
    filter: invert(100);
    width: 1rem;
  }
`;
