          bgClassName={"parallax_bg"}
        >
          <div className="bgImg_shadow" style={{ height: "100vh" }} />
        </Parallax>
      </div> */}

      <BackgroundImg bgImg={bgImg} left={left} />

      <div className="text">
        <div className="title"> {title} </div>
        <div className="description">{description}</div>
      </div>
    </SectionStyle>
  );
}
