import React, { Component } from "react";
import { Link } from "react-router-dom";

import bgLogo from "../3_images/1_home/icon_home-big.png";

import { MenuStyle } from "../2_styles/3_module/MenuStyle";
import Line from "../0_components/Line";

export default class Menu extends Component {
  hideMenuOnClick = () => {
    const links = document.querySelectorAll(".menu_link");
    const menuBox = document.getElementById("menu_box");
    const mainBox = document.getElementById("main_box");
    const menuBtn = document.getElementById("menu_btn");

    links.forEach(link => {
      link.addEventListener("click", function() {
        menuBox.classList.remove("menu_box-show");
        menuBox.classList.add("menu_box-hide");
        mainBox.classList.toggle("main_box-blur");

        menuBtn.classList.toggle("menu_btn-toggle");

        menuActive = false;
      });
    });
  };

  render() {
    return (
      <MenuStyle>
        <div id="menu_box">
          <div className="menu_bgLogo">
            <img id="bgLogo_img" src={bgLogo} alt="avengers logo" />
          </div>
          <div className="menu_items">
            <Link className="link_initiative" to="/the-initiative">
              <div className="menu_link initiative ">The Initiative</div>
            </Link>

            <div className="line menu_line menu_line-top">
              <Line opacity="0.3" width="40vw" />
            </div>

            <Link className="link_original" to="/the-original-six">
              <div className="menu_link original">The Original Six</div>
            </Link>

            <div className="line menu_line delay menu_line-bottom">
              <Line opacity="0.3" width="40vw" />
            </div>

            <Link className="link_become" to="/the-become-an-avenger">
              <div className="menu_link become">Become An Avenger</div>
            </Link>
          </div>
        </div>
      </MenuStyle>
    );
  }
}
