import React from "react";
const FormError = ({ touched, message }) => {
  !touched && <div className="form-message">&nbsp;</div>;

  if (message) {
    return <div className="form-message">{message}</div>;
  }
  return <div className="form-message"></div>;
};

export default FormError;
