import React, { Component } from "react";
import $ from "jquery";

import { BackgroundImgStyle } from "../../2_styles/1_layout/BackgroundImgStyle";

export default class BackgroundImg extends Component {
  componentDidMount() {
    let prevScrollpos = window.pageYOffset;

    window.onscroll = function() {
      if (prevScrollpos > 583) {
        $(window).scroll(function() {
          var scroll = $(window).scrollTop();
          $(".bgImg").css({
            "-webkit-filter": "blur(" + scroll / 200 + "px)",
            filter: "blur(" + scroll / 100 + "px)"
          });
        });
      }
    };
  }

  render() {
    const { left, bgImg, bgFixed } = this.props;
    return (
      <div>
        <BackgroundImgStyle bgImg={bgImg} bgFixed={bgFixed}>
          <div className="bgImg_shadow"></div>
          <div className="bgImg "></div>
        </BackgroundImgStyle>
      </div>
    );
  }
}
