import styled from "styled-components";

export const BackgroundImgStyle = styled.div`
  .bgImg_shadow {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    top: ${props => props.top};
    left: 0;
    z-index: ${props => props.zIndexImgShadow};

    background: linear-gradient(to top, #111111, transparent 60%);
  }

  .bgImg {
    width: 100%;
    height: 500px;

    position: absolute;
    top: ${props => props.top};
    left: 0;
    z-index: ${props => props.zIndexImg};

    background-image: url(${props => props.bgImg});
    background-position: center;
    background-size: cover;
  }
`;
