import styled from "styled-components";

export const MenuStyles = styled.div`
  #menu_box {
    width: 100%;
    height: 0;

    position: absolute;
    top: 0;
    right: 0;

    font-family: "Cormorant Infant", serif;
    font-size: 2em;

    background: #111111;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;

    .link_box {
      height: 70vh;
      position: absolute;
      top: 0;
    }

    .menu_link {
      padding: 0.7em;

      &:nth-child(1) {
        /* padding-top: 3em; */
      }
    }

    .line,
    .menu_link {
      /* transform: translateX(-200%); */
      opacity: 0;
    }
  }

  /* ANIMATIONS ---------- */

  /* MENU BOX */
  #menu_box.menu_box-show {
    animation: menuBox_show 0.7s ease-in-out forwards;
    box-shadow: 0 10px 20px black;
  }

  #menu_box.menu_box-hide {
    animation: menuBox_hide 0.7s ease-in-out forwards;
  }

  @keyframes menuBox_show {
    0% {
      height: 0;
    }
    70% {
      height: 80vh;
    }
    100% {
      height: 70vh;
    }
  }

  @keyframes menuBox_hide {
    0% {
      height: 70vh;
    }
    30% {
      height: 80vh;
    }
    100% {
      height: 0vh;
    }
  }

  /* LINKS */

  .menu_link.menu_link-show {
    animation: menu_link-show 0.7s cubic-bezier(0.65, 0.05, 0.36, 1) forwards;
    animation-delay: 0.3s;
  }
  .menu_link.menu_link-hide {
    animation: menu_link-hide 0.7s cubic-bezier(0.65, 0.05, 0.36, 1) forwards;
  }

  @keyframes menu_link-show {
    0% {
      transform: translateX(-200%);
      opacity: 0;
    }
    70% {
      transform: translateX(10%);
    }
    100% {
      transform: translateX(0%);
      opacity: 1;
    }
  }

  @keyframes menu_link-hide {
    0% {
      transform: translateX(0%);
      opacity: 1;
    }
    30% {
      transform: translateX(10%);
    }
    100% {
      transform: translateX(-200%);
      opacity: 0;
    }
  }
`;
