import styled from "styled-components";

/* .bgImg_shadow {
    width: 100%;
    height: 100vh;
    z-index: -1;

    position: absolute;

    background: linear-gradient(to bottom, transparent, #111111)
      ${props => props.bgFixed};
  }

  .bgImg {
    z-index: -2;
    width: 100%;
    height: 100vh;

    position: absolute;

    background: #212121 url(${props => props.bgImg}) no-repeat fixed center;
    background-size: cover;
  } */

export const BackgroundImgStyle = styled.div`
  .bgImg {
    width: 100%;
    height: 100vh;

    position: absolute;

    background: #212121 url(${props => props.bgImg}) no-repeat fixed center;
    background-size: cover;
  }
`;
