import React, { Component } from "react";

import Parallax from "react-rellax";
import { Formik } from "formik";
import * as Yup from "yup";

import bgImgBanner from "../3_images/4_becomeAnAvenger/bg_become-banner.jpg";
import bgImgForm from "../3_images/4_becomeAnAvenger/bg_become-form.jpg";
// import iconArrow from "../3_images/4_becomeAnAvenger/icon_arrow-form.svg";

import Header from "../0_components/1_layout/Header";
import Footer from "../0_components/1_layout/Footer";
import FormError from "../0_components/2_module/FormError";
import Section from "../0_components/1_layout/Section";

import { BecomeAnAvengerStyle } from "../2_styles/1_layout/BecomeAnAvengerStyle";
import Form from "../0_components/2_module/Form";

const validationSchema = Yup.object().shape({
  superName: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Please enter your superhero name"),
  realName: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Please enter your real name"),
  superpower: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Please enter at least one superpower"),
  country: Yup.string() // CREATE COUNTRY LIST
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Please choose your country"),
  retiredHuman: Yup.string() // BOOLEAN??
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Please select an answer"),
  email: Yup.string()
    .email("Must be a valid email")
    .max(255, "Must be shorter than 255 characters")
    .required("Please enter your email")
});

export default class BecomeAnAvenger extends Component {
  inputOnFocus = () => {
    document.querySelector(".form_bgImg").classList.add("from_bgImg-blur");
  };

  inputOnBlur = () => {
    document.querySelector(".form_bgImg").classList.remove("from_bgImg-blur");
  };

  render() {
    return (
      <div>
        <Header
          bgImg={bgImgBanner}
          title="become an avenger"
          subtitle="join earth’s mightiest heroes"
          bgPosition="80%"
        />

        <BecomeAnAvengerStyle bgImgForm={bgImgForm}>
          <div className="form_bgImg-box">
            <div className="form_bgImg-shadow"></div>
            <div className="form_bgImg"></div>
          </div>

          <Parallax speed={-1}>
            <Formik
              initialValues={{
                superName: "",
                realName: "",
                superpower: "",
                country: "",
                retiredHuman: "",
                email: ""
              }}
              validationSchema={validationSchema}
              onSubmit={(values, { setSubmitting, resetForm }) => {
                setSubmitting(true);
                setTimeout(() => {
                  alert(JSON.stringify(values, null, 2));
                  resetForm();
                  setSubmitting(false);
                }, 500);
              }}
            >
              {({
                values,
                errors,
                touched,
                handleChange,
                handleBlur,
                handleSubmit,
                isSubmitting
              }) => (
                <form id="myForm" onSubmit={handleSubmit}>
                  <div className="input_row">
                    <label htmlFor="superName">
                      What is your superhero name?
                    </label>
                    <input
                      type="text"
                      name="superName"
                      id="superName"
                      placeholder="Chapolin Colorado"
                      onFocus={this.inputOnFocus}
                      onChange={handleChange}
                      onBlur={(handleBlur, this.inputOnBlur)}
                      value={values.superName}
                      className={
                        touched.superName && errors.superName
                          ? "input_error"
                          : null
                      }
                    />
                    <FormError
                      touched={touched.superName}
                      message={errors.superName}
                    />
                  </div>

                  <div className="input_row">
                    <label htmlFor="realName">What is your real name?</label>
                    <input
                      type="text"
                      name="realName"
                      id="realName"
                      placeholder="Peter Parker"
                      onFocus={this.inputOnFocus}
                      onChange={handleChange}
                      onBlur={(handleBlur, this.inputOnBlur)}
                      value={values.realName}
                      classname={
                        touched.realName && errors.realName
                          ? "input_error"
                          : null
                      }
                    />
                    <FormError
                      touched={touched.superName}
                      message={errors.superName}
                    />
                  </div>

                  <div className="input_row">
                    <label htmlFor="superpower">
                      What are your superpowers?
                    </label>
                    <input
                      type="text"
                      name="superpower"
                      id="superpower"
                      placeholder="Fly, super-strenght, lick your elbow. That kind of thing."
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.superpower}
                      classname={
                        touched.superpower && errors.superpower
                          ? "input_error"
                          : null
                      }
                    />
                    <FormError
                      touched={touched.superName}
                      message={errors.superName}
                    />
                  </div>

                  <div className="input_row">
                    <label htmlFor="country">
                      What country do you live in?
                    </label>
                    <input
                      type="text"
                      name="country"
                      id="country"
                      placeholder="Wakanda"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.country}
                      classname={
                        touched.country && errors.country ? "input_error" : null
                      }
                    />
                    <FormError
                      touched={touched.superName}
                      message={errors.superName}
                    />
                  </div>

                  <div className="input_row input_row-retiredHuman">
                    <label htmlFor="retiredHuman">
                      Have you ever retired a human by mistake?
                    </label>
                    <div className="retiredHuman_box">
                      <input
                        type="radio"
                        name="retiredHuman"
                        id="retiredHuman_yes"
                        onChange={handleChange}
                        onBlur={handleBlur}
                        value={values.retiredHuman}
                        classname={
                          touched.retiredHuman && errors.retiredHuman
                            ? "input_error"
                            : null
                        }
                      />
                      <span>Yes</span>

                      <input
                        type="radio"
                        name="retiredHuman"
                        id="retiredHuman_no"
                        onChange={handleChange}
                        onBlur={handleBlur}
                        value={values.retiredHuman}
                        classname={
                          touched.retiredHuman && errors.retiredHuman
                            ? "input_error"
                            : null
                        }
                      />
                      <span>No</span>
                    </div>
                    <FormError
                      touched={touched.superName}
                      message={errors.superName}
                    />
                  </div>

                  <div className="input_row">
                    <label htmlFor="email">What's your email?</label>
                    <input
                      type="email"
                      name="email"
                      id="email"
                      placeholder="email@example.com"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.email}
                      classname={
                        touched.email && errors.email ? "input_error" : null
                      }
                    />
                    <FormError
                      touched={touched.superName}
                      message={errors.superName}
                    />
                  </div>

                  <div className="btn_submit-box">
                    <button
                      id="btn_submit"
                      type="submit"
                      disabled={isSubmitting}
                    >
                      Submit
                    </button>
                  </div>
                </form>
              )}
            </Formik>
          </Parallax>
        </BecomeAnAvengerStyle>

        <Footer />
      </div>
    );
  }
}
