import bgImgBanner from "../3_images/4_becomeAnAvenger/bg_become-banner.jpg";
import bgImgForm from "../3_images/4_becomeAnAvenger/bg_become-form.jpg";

import Header from "../0_components/1_layout/Header";
import Footer from "../0_components/1_layout/Footer";

import { BecomeAnAvengerStyle } from "../2_styles/1_layout/BecomeAnAvenger";

export default function BecomeAnAvenger() {
  return (
    <div>
      <Header
        bgImg={bgImgBanner}
        title="become an avenger"
        subtitle="join earthâ€™s mightiest heroes"
        bgPosition="80%"
      />

      <BecomeAnAvengerStyle bgImgForm={bgImgForm}>
        <div className="form_box">
          <div className="form_bgImg"></div>
          <div className="form_input-box">
            <p>What's your superhero name?</p>
            <input placeholder="Chapolin Colorado" type="text" />
            <div className="line"></div>
            <div className="arrow"></div>
          </div>
          <div className="dots_box">
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
          </div>
        </div>
      </BecomeAnAvengerStyle>

      <Footer />
    </div>
  );
}

