import React, { Component } from "react";

import Header from "../0_components/1_layout/Header";
import Section from "../0_components/1_layout/Section";
import Footer from "../0_components/1_layout/Footer";

const contentful = require("contentful");

export default class TheOriginalSix extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      images: {
        header: { small: "", big: "" },
        icons: {
          cap: "",
          iron: "",
          thor: "",
          hulk: "",
          widow: "",
          hawk: ""
        },
        banners: {
          cap: "",
          iron: "",
          thor: "",
          hulk: "",
          widow: "",
          hawk: ""
        }
      }
    };
  }

  getData = async () => {
    const client = contentful.createClient({
      space: "b4lkqx9hlu6y",
      accessToken: "7ehrQIlJJLgAlYL9qMa4CeLdxIdiUJWrbtPOWpQThBc"
    });

    client
      .getEntries({ content_type: "theOriginalSix" })
      .then(response => {
        this.setState({
          data: response.items[0].fields
        });

        this.setState({
          images: {
            header: {
              small: this.state.data.bgImgHeader[0].fields.file.url,
              big: this.state.data.bgImgHeader[1].fields.file.url
            },
            icons: {}
          }
        });
      })
      .catch(console.error);
  };

  componentDidMount() {
    this.getData();
  }

  render() {
    const { data, images } = this.state;
    const screenWidth = window.screen.width;

    return (
      <div>
        <Header
          the="the"
          bgImg={screenWidth < 600 ? images.header.small : images.header.big}
          title={data.title}
          subtitle={data.subtitle}
          bgPosition="20%"
        />

        <Footer />
      </div>
    );
  }
}
