import React, { Component } from "react";

import { MenuButtonStyle } from "../../2_styles/3_module/0_buttons/MenuButtonStyle";
import Menu from "../2_module/Menu";

export default class MenuButton extends Component {
  componentDidMount() {
    this.handleAnimation();
  }

  toggleMenu = () => {
    this.props.toggleMenu();
  };

  handleAnimation = () => {
    const menuBtn = document.getElementById("menu_btn");
    const currentUrl = window.location.href;
    const homeUrl = "http://localhost:3000/";

    currentUrl === homeUrl
      ? menuBtn.classList.add("menu_btn-animate")
      : (document.getElementById("menu_btn").style.opacity = "1");
  };

  render() {
    const { disabled } = this.props;

    return (
      <div>
        <div id="menu_btn-box">
          <MenuButtonStyle>
            <button
              id="menu_btn"
              className="menu_btn-animate"
              onClick={this.toggleMenu}
              disabled={disabled}
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </MenuButtonStyle>
        </div>
        <Menu toggleMenu={this.toggleMenu} />
      </div>
    );
  }
}
