import React, { Component } from "react";

import { MenuButtonStyle } from "../../2_styles/3_module/0_buttons/MenuButtonStyle";
import Menu from "../Menu";

let menuActive = false;

export default class MenuButton extends Component {
  constructor() {
    super();
    this.toggleMenu = this.toggleMenu.bind(this);
  }

  toggleMenu() {
    const menuBox = document.getElementById("menu_box");

    if (!menuActive) {
      if (menuBox.classList.contains("hide")) {
        menuBox.classList.remove("hide");
      }
      menuBox.classList.add("open");
    } else {
      menuBox.classList.remove("open");
      menuBox.classList.add("hide");
    }
  }

  render() {
    return (
      <div>
        <div id="menu_btn-box">
          <MenuButtonStyle>
            <button id="menu_btn" onClick={this.toggleMenu}>
              <span></span>
              <span></span>
              <span></span>
            </button>
          </MenuButtonStyle>
        </div>
        <Menu />
      </div>
    );
  }
}
