import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { FormStyle } from "../../2_styles/3_module/FormStyle";

export default class Form extends Component {
  render() {
    return (
      <div>
        <Formik>
          <FormStyle>
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting
            }) => (
              <form id="myForm">
                <div className="input_row">
                  <label htmlFor="superName">
                    What's is your superhero name?
                    <input
                      type="text"
                      name="superName"
                      id="superName"
                      placeholder="Chapolin Colorado"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.name}
                      className={
                        touched.name && errors.name ? "has-error" : null
                      }
                    />
                  </label>
                </div>
              </form>
            )}
          </FormStyle>
        </Formik>
      </div>
    );
  }
}
