import styled from "styled-components";

export const BackgroundImgStyle = styled.div`
  .bgImg {
    min-height: 100vh;
    width: 100vw;

    position: relative;
    box-sizing: border-box;
    transform-style: inherit;
  }

  .bgImg,
  .bgImg:before {
    background: 50% 50% / cover;
  }
`;

