import styled from "styled-components";

/* .bgImg_shadow {
    width: 100%;
    height: 100vh;
    z-index: -1;

    position: absolute;

    background: linear-gradient(to bottom, transparent, #111111)
      ${props => props.bgFixed};
  }

  .bgImg {
    z-index: -2;
    width: 100%;
    height: 100vh;

    position: absolute;

    background: #212121 url(${props => props.bgImg}) no-repeat fixed center;
    background-size: cover;
  } */

export const BackgroundImgStyle = styled.div`
  /* height: 100%; */
  overflow: hidden;

  .bgImg-box {
    height: 100%;

    color: #fff;
    margin: 0;
    padding: 0;

    perspective: 1px;
    transform-style: preserve-3d;

    overflow-y: scroll;
    overflow-x: hidden;

    font-family: "Luna";
  }

  .bgImg {
    width: 100vw;
    min-height: 100vh;

    position: relative;

    padding: 30vw 0 5vw;
    box-sizing: border-box;

    transform-style: inherit;
  }

  .bgImg h1 {
    margin-top: -100px;
  }

  .bgImg,
  .bgImg:before {
    background: 50% 50% / cover;
  }

  .bgImg::before {
    content: "";

    min-height: 100vh;

    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;

    display: block;

    background-image: url(${props => props.bgImg});
    background-size: cover;

    transform-origin: center center 0;
    transform: translateZ(-1px) scale(2);

    z-index: -1;
  }

  .bgImg * {
    font-weight: normal;
    letter-spacing: 0.2em;
    text-align: center;

    margin: 0;
    padding: 1em 0;
  }
`;
