import styled from "styled-components";

export const LineStyle = styled.div`
  width: 80vw;
  height: 1px;

  margin: 5px 0 5px 0;

  opacity: ${props => props.opacity}

  background: linear-gradient(to right, transparent, #f0f0f0, transparent);
`;
