import React from "react";

//COMPONENTS
import Line from "../2_module/Line";
import BackgroundImg from "./BackgroundImg";

//STYLES
import { HeaderStyle } from "../../2_styles/1_layout/HeaderStyle";
import { BackgroundImgStyle } from "../../2_styles/1_layout/BackgroundImgStyle";

export default function Header({ bgImg, bgImgBlur, title, subtitle }) {
  return (
    <div>
      <HeaderStyle>
        {/* <BackgroundImg
          bgImg={bgImg}
          bgImgBlur={bgImgBlur}
          height="100vh"
          zIndexImg="-4"
          zIndexImgShadow="-3"
        /> */}

        <BackgroundImg bgImg={bgImg} />

        <div className="title">
          <p>the</p>
          <p>{title}</p>
          <Line />
          <p>{subtitle}</p>
        </div>
      </HeaderStyle>
    </div>
  );
}
