import React from "react";

import { Parallax, Background } from "react-parallax";
import cu from "../../3_images/2_theInitiative/bg_initiative-banner.jpg";

//COMPONENTS
import Line from "../2_module/Line";
import BackgroundImg from "./BackgroundImg";

//STYLES
import { HeaderStyle } from "../../2_styles/1_layout/HeaderStyle";
import { BackgroundImgStyle } from "../../2_styles/1_layout/BackgroundImgStyle";

export default function Header({ bgImg, title, subtitle }) {
  return (
    <div>
      <HeaderStyle>
        {/* <BackgroundImg
          bgImg={bgImg}
          height="100vh"
          zIndexImg="-4"
          zIndexImgShadow="-3"
        /> */}
        <Parallax
          bgImage={"../../3_images/2_theInitiative/bg_initiative-banner.jpg"}
          strength={400}
          renderLayer={percentage => (
            <div
              style={{
                position: "absolute",
                background: `rgba(255, 125, 0, ${percentage * 1})`,
                left: "50%",
                top: "50%",
                width: percentage * 500,
                height: percentage * 500
              }}
            />
          )}
        >
          <p>... Content</p>
        </Parallax>

        <div className="title">
          <p>the</p>
          <p>{title}</p>
          <Line />
          <p>{subtitle}</p>
        </div>
      </HeaderStyle>
    </div>
  );
}
