import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";

//COMPONENTS
import HomeButton from "./0_components/0_buttons/HomeButton";

// PAGES
import Home from "./1_pages/Home";
import TheInitiative from "./1_pages/TheInitiative";
import TheOriginalSix from "./1_pages/TheOriginalSix";
import BecomeAnAvenger from "./1_pages/BecomeAnAvenger";

//STYLES
import { createGlobalStyle } from "./2_styles/0_base/GlobalStyle";

function App() {
  return (
    <Router>
      <div id="App">
        <HomeButton />

        <Route
          render={({ location }) => (
            <TransitionGroup>
              <CSSTransition
                key={location.key}
                timeout={3000}
                classNames="fade"
              >
                <Switch location={location}>
                  <Route path="/" exact component={Home} />
                  <Route path="/the-initiative" component={TheInitiative} />
                  <Route path="/the-original-six" component={TheOriginalSix} />
                  <Route
                    path="/become-an-avenger"
                    component={BecomeAnAvenger}
                  />
                </Switch>
              </CSSTransition>
            </TransitionGroup>
          )}
        />

        {/* <MenuButton /> */}
      </div>
      <GlobalStyle />
    </Router>
  );
}

export default App;
