import styled from "styled-components";

export const MenuButtonStyle = styled.div`
  #menu_btn {
    z-index: 1;

    width: 30px;
    height: 25px;

    position: fixed;
    top: 0;
    right: 0;

    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-end;

    /* margin: 5vw 5vw 0 0; */

    span {
      width: 30px;
      height: 1px;

      border-radius: 50px;
      background-color: #f0f0f0;
    }

    cursor: pointer;

    /* &:hover {
      span {
        :nth-child(1) {
          width: 100%;
        }
        :nth-child(2) {
          width: 90%;
        }
        :nth-child(3) {
          width: 100%;
        }
      }
    }

    span {
      width: 5px;
      height: 5px;

      transition: 0.3s cubic-bezier(0.8, 0.5, 0.2, 1.4);

      position: absolute;

      border-radius: 50px;
      background-color: #f0f0f0;

      &:nth-child(1) {
        top: 0px;
        right: 0px;
      }
      &:nth-child(2) {
        top: 10px;
        right: 0px;
        opacity: 1;
      }
      &:nth-child(3) {
        bottom: 0px;
        right: 0px;
      }
    } */
  }
`;

