import styled from "styled-components";

export const BackgroundImgStyle = styled.div`
  .bgImg_shadow {
    width: 100%;
    height: 100vh;
    z-index: -1;

    position: absolute;

    background: linear-gradient(to bottom, transparent, #111111);
  }

  .bgImg {
    z-index: -2;
    width: 100%;
    height: 100vh;

    position: absolute;

    background: #212121 url(${props => props.bgImg}) no-repeat fixed center;
    background-size: cover;
  }
`;

