import React, { Component } from "react";

import { MenuButtonStyle } from "../../2_styles/3_module/0_buttons/MenuButtonStyle";
import Menu from "../Menu";

let menuActive = false;

export default class MenuButton extends Component {
  constructor() {
    super();
    this.toggleMenu = this.toggleMenu.bind(this);
  }

  toggleMenu() {
    const menuBox = document.getElementById("menu_box");
    const linkInitiative = document.getElementById("link_initiative");

    if (!menuActive) {
      // menuBox.classList.contains("menu_box-hide") &&
      //   menuBox.classList.remove("menu_box-hide");
      // menuBox.classList.add("menu_box-show");

      linkInitiative.classList.contains("link_initiative-hide") &&
        linkInitiative.classList.remove("link_initiative-hide");
      linkInitiative.classList.add("link_initiative-show");

      menuActive = true;
    } else {
      // menuBox.classList.remove("menu_box-show");
      // menuBox.classList.add("menu_box-hide");

      linkInitiative.classList.remove("link_initiative-show");
      linkInitiative.classList.add("link_initiative-hide");

      menuActive = false;
    }
  }

  render() {
    return (
      <div>
        <div id="menu_btn-box">
          <MenuButtonStyle>
            <button id="menu_btn" onClick={this.toggleMenu}>
              <span></span>
              <span></span>
              <span></span>
            </button>
          </MenuButtonStyle>
        </div>
        <Menu />
      </div>
    );
  }
}
