import React, { Component } from "react";

import HomeButton from "./HomeButton";
import MenuButton from "./MenuButton";

import { NavbarStyle } from "../../2_styles/3_module/NavbarStyle";

export default class Navbar extends Component {
  constructor() {
    super();
    this.state = { pageName: "" };
  }
  componentDidMount = () => {
    if (window.location.href === "http://localhost:3000/the-initiative") {
      this.setState({ pageName: "the initiative" });
    }
  };

  render() {
    const { pageName } = this.state;

    return (
      <div>
        <NavbarStyle>
          <div id="navbar">
            <HomeButton />
            <div id="pageName">{pageName}</div>
            <MenuButton />
          </div>
        </NavbarStyle>
      </div>
    );
  }
}
