import styled from "styled-components";

export const FormStyle = styled.div`
  .myForm {
    height: 100vh;
    padding: 0 5vw 0 5vw;

    display: flex;
    align-items: center;
    flex-wrap: wrap;
  }

  .input_row {
    width: calc(100% - 2rem);

    display: flex;
    flex-direction: column;
    margin-top: 7rem;

    display: none;

    &:nth-child(1) {
      display: flex;
    }
  }

  label {
    font-size: 2rem;
    font-family: "cormorant infant", serif;
  }
  input {
    padding: 1rem 1rem 1rem 0;

    background: linear-gradient(to right, #f0f0f0, transparent) left bottom
      no-repeat;
    background-size: 100% 1px;

    font-size: 1.5rem;
    font-family: "roboto", sans-serif;
    font-weight: 200;
    text-transform: uppercase;

    ::placeholder {
      color: #c7c7c7;
    }
  }

  #nextQuestion_button {
    cursor: pointer;

    width: 2rem;

    margin-top: 9rem;
    float: left;
    text-align: center;
  }

  #nextQuestion_button-icon {
    filter: invert(100);
    width: 0.8rem;
  }

  .dots_box {
    display: flex;
    justify-content: center;
    align-items: center;

    margin: auto;
    margin-top: -5rem;
  }

  .dot {
    background: #f0f0f0;
    width: 0.5rem;
    height: 0.5rem;
    border-radius: 100%;
    margin: 1rem;
  }

  /* ERRORS -------------------------------------- */

  .input_error {
    background: linear-gradient(to right, crimson, transparent) left bottom
      no-repeat;
    background-size: 100% 1px;
  }

  .form_error-message {
    margin-top: 1rem;
    color: crimson;
    font-weight: 300;
  }
`;
