import styled from "styled-components";

export const HomeButtonStyle = styled.div`
  #home_btn {
    z-index: 1;
    cursor: pointer;
    /* position: fixed;
    top: 0;
    left: 0; */

    /* margin: 6vw 0 0 5vw; */
    margin-left: 5vw;
  }

  img {
    opacity: 0.5;
    width: 25px;
    transition: 0.3s ease-out;

    &:hover {
      opacity: 0.8;
    }
  }
`;
