import styled from "styled-components";

export const BecomeAnAvengerStyle = styled.div`
  /* FORM BACKGROUND IMAGE ----------------------------- */
  .form_bgImg-box {
    width: 100%;
    height: 150vh;

    position: absolute;
    overflow: hidden;
    z-index: -1;
  }

  .form_bgImg {
    width: 100%;
    height: 150vh;

    position: relative;

    background-image: url(${props => props.bgImgForm});
    background-size: cover;
    background-position: 40%;

    filter: brightness(70%);
    transition: 0.3s ease-in-out;
  }

  .from_bgImg-blur {
    filter: brightness(70%) blur(3px);
    transform: scale(1.5);
  }

  /* FORM ----------------------------- */
  #myForm {
    height: 150vh;
    padding: 0 5vw 0 5vw;

    display: flex;
    /* align-items: center; */
    flex-wrap: wrap;
  }

  .input_row {
    /* background: grey; */

    width: 100%;

    /* display: flex; */
    /* flex-direction: column; */
  }

  .input_row-retiredHuman {
    width: 100%;
    flex-direction: row;
    flex-wrap: wrap;
  }

  #retiredHuman_yes,
  #retiredHuman_no,
  span {
    margin: 1rem 1rem 0.5rem 0;
  }

  span {
    color: #f0f0f0;
    font-weight: 300;
  }

  label {
    font-size: 1.3rem;
    font-family: "cormorant infant", serif;
  }
  input {
    padding: 1rem 1rem 0.5rem 0;

    font-size: 1rem;
    font-family: "roboto", sans-serif;
    font-weight: 300;
    text-transform: uppercase;

    ::placeholder {
      color: #c7c7c7;
      font-weight: 100;
    }
  }

  .retiredHuman_box {
    width: 100%;
  }

  input,
  .retiredHuman_box {
    /* background: linear-gradient(to right, #f0f0f0, transparent) left bottom
      no-repeat;
    background-size: 100% 1px; */
    border: 1px solid #f0f0f0;
  }

  .btn_submit-box {
    width: 100%;

    display: flex;
    justify-content: flex-end;
  }

  #btn_submit {
    cursor: pointer;
    text-align: end;
  }

  /* ERRORS -------------------------------------- */

  .input_error {
    background: linear-gradient(to right, crimson, transparent) left bottom
      no-repeat;
    background-size: 100% 1px;
  }

  .form_error-message {
    margin-top: 1rem;
    margin-bottom: -5rem;
    color: red;
    font-weight: 300;
    font-size: 0.7rem;
  }
`;
