import styled from "styled-components";

export const DotStyle = styled.div`
  /* #nav_dots {
    height: 100vh;
    width: 100%;

    position: absolute;
    top: 4vh;

    display: flex;
    justify-content: center;
    z-index: 2;
  }

  .dots_box {
    position: fixed;

    display: flex;
    align-items: center;

    transition: 0.5s ease-in-out;
  }

  .dots {
    width: 0.6rem;
    height: 0.6rem;

    margin-left: 1rem;

    opacity: 0.5;
    border: 1px solid #f0f0f0;
    border-radius: 100%;
    cursor: pointer;

    transition: 0.3s ease-in-out;

    &:hover {
      opacity: 1;
      background: #f0f0f0;
    }
  }

  .dot_active {
    opacity: 1;

    background: #f0f0f0;
  }

  @media only screen and (min-width: 600px) {
    #nav_dots {
      top: 9vh;
    }
  } */

  .dots_box {
    width: 100%;
    height: 100vh;

    position: fixed;
    top: 0;

    display: flex;
    justify-content: center;
    z-index: 2;

    & > div {
      width: 0.6rem;
      height: 0.6rem;

      margin: 1rem 0 0 1rem;

      opacity: 0.5;
      cursor: pointer;

      border: 1px solid #f0f0f0;
      border-radius: 100%;

      &:nth-child(1) {
        margin-left: 0;
      }

      transition: 0.3s ease-out;
      &:hover {
        background: #f0f0f0;
      }
    }
  }
`;
