import styled from "styled-components";

export const BackToTopStyle = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

  .arrow_box {
    display: flex;
    justify-content: center;
    align-items: center;

    cursor: pointer;
    width: 50px;
    height: 50px;
    background-color: red;

    margin-top: 2em;
  }
  .arrow_right,
  .arrow_left {
    width: 50%;
    height: 1px;
    background-color: #f0f0f0;
  }

  .arrow_right {
    transform: rotate(-45deg);
    /* margin-left: 17px; */
  }

  .arrow_left {
    /* transform: rotate(-45deg); */
  }
`;
